RosComm::RosComm(ComponentMain* comp,int argc,char** argv)
{
  ros::init(argc,argv,"PP_node");
  _comp=comp;
	_sub_Map=ros::Subscriber(_nh.subscribe(fetchParam(&_nh,"SE_R2U","Sensor_WIRE","sub"), 10, &RosComm::Sensor_WIRECallback,this));
	_sub_Map=ros::Subscriber(_nh.subscribe(fetchParam(&_nh,"SE_R2U","Sensor_INSGPS","sub"), 10, &RosComm::Sensor_INSGPSCallback,this));

}
RosComm::~RosComm()
{
}

void RosComm::Sensor_WIRECallback(const config::SE_R2U::sub::Sensor_WIRE::ConstPtr &msg)
{
	_comp->handleSensor_WIRE(*msg);
}
	

void RosComm::Sensor_INSGPSCallback(const config::SE_R2U::sub::Sensor_INSGPS::ConstPtr &msg)
{
	_comp->handleSensor_INSGPS(*msg);
}
	

